import react, {useState, useEffect} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import styled from 'styled-components'
import Logo from '../assets/logo.png'
import axios from 'axios';
import Contacts from './Contacts';

import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';
import { registerRoute, allUsersRoute } from '../utils/APIRoutes';


function Chat(){

    const loadUsers = async ()=> {
        console.log("Auth token in header " + user.user.token)
        const headers = { 'authorization': `Bearer ${user.user.token}` };
        axios.get(allUsersRoute, {"id":""}, {headers}).then((response)=>{
            console.log(response.data);
        })
    }

var [contacts, setContacts] = useState([])

  const navigate = useNavigate();

  const [currentUser, setCurrentUser] = useState(undefined);
  const user = JSON.parse(localStorage.getItem('chat-app-user'))
  console.log("Token is " + user.token)
  loadUsers();
  useEffect( () => {
    if (!localStorage.getItem('chat-app-user')) {
      navigate("/login");
    } else {
      setCurrentUser(
        JSON.parse(
          localStorage.getItem('chat-app-user')
        )
      );
    }
  }, []);


//   useEffect( ()=>{
//     loadUsers();
//   })



    return(

            <Container>
                <div className='container'>
                    <Contacts contacts={contacts} currentUser={user.user}></Contacts>
                </div>
            </Container>
    );
}

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;
  .container {
    height: 85vh;
    width: 85vw;
    background-color: #00000076;
    display: grid;
    grid-template-columns: 25% 75%;
    @media screen and (min-width: 720px) and (max-width: 1080px) {
      grid-template-columns: 35% 65%;
    }
  }
`;

export default Chat;
