    return(
        <Container className="container">
            <img src={Robot} alt="Robot"></img>
            <h1>Welcome <span>{currentUser.username}!</span></h1>
            <h3>Please select a chat to start messging</h3>

        </Container>
    )
}


const Container = styled.div`

