// import react from 'react'
import react, {useState, useEffect} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import styled from 'styled-components'
import Logo from '../assets/logo.png'
import axios from 'axios';
import Contacts from './Contacts';

import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';
import { registerRoute, allUsersRoute } from '../utils/APIRoutes';


function Chat(){



var [contacts, setContacts] = useState([])

  const navigate = useNavigate();

  const [currentUser, setCurrentUser] = useState(undefined);
  const user = JSON.parse(localStorage.getItem('chat-app-user'))
  console.log("Token is " + user.token)

  useEffect( () => {

    const loadUsers = async(token)=> {
      console.log("Auth token in header " + token)
      const headers = { 'Authorization': `Bearer ${token}` };
      axios.get(allUsersRoute, {headers}).then((response)=>{
          console.log(response.data);
          setContacts(response.data.data)
      })
    }
    const loadCurrentUser = useCallback(async()=>{
      if (!localStorage.getItem('chat-app-user')) {
        navigate("/login");
      } else {
        setCurrentUser(
          JSON.parse(
            localStorage.getItem('chat-app-user')
          )
        );
        loadUsers(currentUser.token);
      }
    });
    loadCurrentUser();


  // loadUsers(user.token);

  }, [loadCurrentUser]);


//   useEffect( ()=>{
//     loadUsers();
//   })



    return(

            <Container>
                <div className='container'>
                    <Contacts contacts={contacts} currentUser={user.user}></Contacts>
                </div>
            </Container>
    );
}

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;
  .container {
    height: 85vh;
    width: 85vw;
    background-color: #00000076;
    display: grid;
    grid-template-columns: 25% 75%;
    @media screen and (min-width: 720px) and (max-width: 1080px) {
      grid-template-columns: 35% 65%;
    }
  }
`;

export default Chat;
